function init(custom) {
    count1 = new CountUp('counter1', 0, custom.countup.attendees.number, 0, 3, special);
    count2 = new CountUp('counter2', 0, custom.countup.hours.number, 0, 3, options);
    count3 = new CountUp('counter3', 0, custom.countup.opportunity.number, 0, 3, options);
    var stuff = [{
        selector: '#counter1',
        offset: 0,
        callback: 'countUp()'
    }];
    Materialize.scrollFire(stuff);
}

