// navbar color
$(window).scroll(function (event) {
    $('nav').removeClass('z-depth-0');

    var scroll = $(window).scrollTop();
    if (scroll <= 0)
        $('nav').addClass('z-depth-0');

    var scale = Math.max(0, Math.min(1, scroll / $(window).height()));
    $('nav').css('background-color', 'rgb(255, 255, 255, ' + scale + ')');
    $('.brand-logo').css('opacity', scale);

    var finalR = 1;		// change these values to change what color
    var finalG = 138;		// the white text becomes as the user scrolls down
    var finalB = 123;

    $('.nav-text, .menu-btn .fa-bars').css('color', rgbToHex(255 - (255 - finalR) * scale, 255 - (255 - finalG) * scale, 255 - (255 - finalB) * scale));
});

var easingFn = function (e, n, o, t) {
        var a = (e /= t) * e,
            s = a * e;
        return n + o * (s + -3 * a + 3 * e)
    },
    options = {
        useEasing: !0,
        useGrouping: !1,
        separator: ',',
        decimal: '.',
        prefix: '',
        suffix: ''
    },
    special = {
        useEasing: !0,
        useGrouping: !1,
        separator: ',',
        decimal: '.',
        prefix: '',
        suffix: '+'
    };

var count1, count2, count3;

function countUp() {
    count1.start();
    count2.start();
    count3.start()
}

function componentToHex(c) {
    var hex = c.toString(16);
    return hex.length == 1 ? '0' + hex : hex;
}

function rgbToHex(r, g, b) {
    return '#' + componentToHex(Math.round(r)) + componentToHex(Math.round(g)) + componentToHex(Math.round(b));
}